import React, {useState} from 'react'
import classes from './display.module.css'
import unavailablePoster from './../img/unavailablePoster.jpeg'
import LikeDislike from '../movieStorage/Like'


const Display = (props) => {

const [sendLikeStorage, setSendLikeStorage] = useState(false)
const posterSrc = props.movies.Poster==="N/A" ? unavailablePoster : props.movies.Poster;
const posterSize = props.movies.Poster==="N/A" ? classes.defaultPoster : '';

const likeHandler = () => {
    // console.log(props.movies)
    setSendStorage(true);
}

return(<React.Fragment>
    <div className={classes.moviesList}>

    <div>

    {<img src={posterSrc} alt='poster' className={posterSize}/>}
        <div>{props.movies.Title}</div>

        <div className={classes.eachMovBtn}>
        <button onClick={likeHandler}>
            Favrouite
        </button>

        <button>
            Dislike
        </button>
        </div>
        {sendLikeStorage && <LikeDislike onMovies={props.movies}/> }
    </div>
    </div>
</React.Fragment>)

}

export default Display
