import React, { useState, useEffect } from "react";
import Display from "./display";

const Catchdisplay = (props) => {
  const [allMovies, setAllMovies] = useState([]);
  const [httpError, setHttpError] = useState(false)
  const[errorMessage, setErrorMessage] = useState('')
  const [loadingError, setloadingError] = useState(false)

  useEffect(() => {
    const fetchMovies = async () => {
      const response = await fetch(
        `http://www.omdbapi.com/?s=${props.onChange}&apikey=10cf3ef8`
      );




      const responseData = await response.json();


      if (responseData.Response==="False")
      {
          setHttpError(true)
          throw new Error('Something went wrong')
      }
      console.log("ResonseData:");
      console.log(responseData);
       const movies = responseData.Search;
      setAllMovies(movies);
    };
    fetchMovies(props.onChange)
    .catch((Error)=>{
           setErrorMessage(Error)
           console.log(loadingError)
           console.log(httpError)
        }
    );
  }

  , [props]);

  if (loadingError)
  {
      return (<p>Loading...</p>)
  }

  if (httpError){
      return(
            <p>Couldn't find any movie of this name. 
              Please enter a new movie name.</p>
          )
  }

  return (
    <React.Fragment>

      {allMovies.map((movie) => (
        <Display key={Math.random()} movies={movie} />
      ))}
    </React.Fragment>
  );
};

export default Catchdisplay;
