import React, { useState } from "react";
import classes from "./display.module.css";
import unavailablePoster from "./../img/unavailablePoster.jpeg";
import Like from "../movieStorage/Like";
import Dislike from "../movieStorage/Dislike";

const Display = (props) => {
  const [sendLikeStorage, setSendLikeStorage] = useState(false);
  const [sendDislikeStorage, setSendDislikeStorage] = useState(false);

  const posterSrc =
    props.movies.Poster === "N/A" ? unavailablePoster : props.movies.Poster;
  const posterSize = props.movies.Poster === "N/A" ? classes.defaultPoster : "";

  const likeHandler = () => {
    setSendLikeStorage(true);

    const likeConfirmHandler = async () => {
        const response  =await fetch("https://mymovielist-7f102-default-rtdb.firebaseio.com/likedmovies",{
            method:'POST',
            body:JSON.stringify({user:props.onMovies})
        })
    }
  };

  const disLikeHandler = () => {
    setSendDislikeStorage(true)
  }

  return (
    <React.Fragment>
      <div className={classes.moviesList}>
        <div>
          {<img src={posterSrc} alt="poster" className={posterSize} />}
          <div>{props.movies.Title}</div>

          <div className={classes.eachMovBtn}>
            <button onClick={likeHandler}>Favrouite</button>

            <button onClick={disLikeHandler}>Dislike</button>
          </div>
          {/* {sendLikeStorage && <Like onMovies={props.movies} />}
          {sendDislikeStorage && <Dislike onMovies={props.movies}/>} */}
        </div>
      </div>
    </React.Fragment>
  );
};

export default Display;
