import {addDoc, collection, getDocs, doc, deleteDoc} from "firebase/firestore"


const Display = (props) => {
  const [sendLikeStorage, setSendLikeStorage] = useState(false);
  const [sendDislikeStorage, setSendDislikeStorage] = useState(false);
  const userCollectionRef = collection(db, "users")
  const userCollectionReff = collection(db, "dislikeUsers")
  const [usersLike, setUsersLike] = useState([])
  const [usersDislike, setUsersDislike] = useState([])
  const [deletingLike,setDeletingLike] = useState([])

  useEffect(()=>{
    const getUsersLike = async () => {
      const data = await getDocs(userCollectionRef)
      setUsersLike(data.docs.map((doc)=>({...doc.data(), id:doc.id})))

  }
  getUsersLike()
  },[userCollectionRef], [])

  useEffect(()=>{
    const getUsersDislike = async () => {
      const data = await getDocs(userCollectionReff)

      setUsersDislike(data.docs.map((doc)=>({...doc.data(), id:doc.id})))

    }
    getUsersDislike()


  },[userCollectionReff],[])



  const createUserLike = async () => {
    setSendLikeStorage(true);
    await addDoc(userCollectionRef, {
        Title:props.movies.Title,
        Type:props.movies.Type,
        Poster:props.movies.Poster,
        Year:props.movies.Year,
        imdbId:props.movies.imdbID,

    })
  }

  const createUserDislike = async () => {
    setSendDislikeStorage(true)
    setSendLikeStorage(false);
    await addDoc(userCollectionReff, {
        Title:props.movies.Title,
        Type:props.movies.Type,
        Poster:props.movies.Poster,
        Year:props.movies.Year,
        imdbId:props.movies.imdbID,

    })
  }





  const posterSrc =
    props.movies.Poster === "N/A" ? unavailablePoster : props.movies.Poster;
  const posterSize = props.movies.Poster === "N/A" ? classes.defaultPoster : "";



  const removelikeHandler = (imdbID) =>{

    setSendLikeStorage(false);
    // const remove=()=>{
