    console.log(usersLike)
     usersLike.map(user=> (console.log("&&&&&&"+user.imdbId)))
     console.log("++++++"+imdbID)

    console.log("++++"+JSON.stringify(usersLike.filter(user => user.imdbId===imdbID)))

    const updateUserLike = async (id) => {
      const userDoc = doc(db, "users", id)
      await deleteDoc(userDoc)
    }

    updateUserLike(deletingLike.id)
    deletingLike.map((d)=>(console.log(d.id)))
    // console.log(deletingLike.id)


