import React, { useState } from "react";
import DislikeDisplay from "./DislikeDisplay";
import classes from './like.module.css'

const Dislike = () => {
  const allKeys = Object.keys(localStorage);
  const [clearAll, setClearAll]= useState(false)

  if (allKeys.length===0){
    setClearAll(true)
    console.log(allKeys.length)
  }

  const clearHandler = () => {
    allKeys.filter((key)=>
      key.substring(key.length - 7)=== "dislike"
  ).map(filteredKey => (
     localStorage.removeItem(filteredKey)
   ))
   setClearAll(true)
  }


  return (
    <React.Fragment>
         <div className={classes.title}> Your "NOT SO" Favrouites!!</div>
         {!clearAll && <button className={classes.clear} onClick={clearHandler}>Clear All</button>}
        <div className={classes.allMovies}>
        {allKeys.filter((key)=>
         key.substring(key.length - 7)=== "dislike"
     ).map(filteredKey => (
        <DislikeDisplay key={Math.random()} movies={filteredKey}/>
      ))}
        </div>


    </React.Fragment>
  );
};

export default Dislike;
