import classes from "./Homebtn.module.css";
import React, { useState } from "react";
import Catchdisplay from "../moviesList/catchDisplay";

const Homebtn = () => {
  const [searchBar, setSearchBar] = useState(false);
  const [choosenMovieName, setChoosenMovieName] = useState("");
  const [correctUserInput, setcorrectUserInput] = useState(false);

  const searchBarHandler = () => {
    setSearchBar(true);
  };
  const allMoviesHandler = () => {
    setcorrectUserInputName(true);
  };

  const formSubmitHandler = (event) => {
    setcorrectUserInputName(event.target.value);
    console.log(correctUserInputName);
    setChosenMovie(false);
  };

  const formHandler = (event) => {
    event.preventDefault();

    setChosenMovie(true);
    console.log("****");
    setSearchBar(false);
  };

  return (
    <React.Fragment>
      <div className={classes.topic}>Your Movies List!</div>
      <div className={classes.btngroup}>
        {!searchBar && (
          <button className={classes.search} onClick={searchBarHandler}>

            Search Movies
          </button>
        )}
        {searchBar && (
          <form onSubmit={formHandler}>
            <input
              type="text"
              placeholder="What movie are you looking for?"
              onChange={formSubmitHandler}
              value={correctUserInputName}
            />
            <button type="submit">Search</button>
          </form>
        )}
        <button className={classes.fav}> Favrouite Movies</button>
        <button className={classes.dislike}> Disliked Movies</button>
      </div>

      {correctUserInput && (
        <div>
          <Catchdisplay onChange={correctUserInputName} />
        </div>
      )}
    </React.Fragment>
  );
};

export default Homebtn;
