import React, {useState} from 'react'
import Catchdisplay from './components/moviesList/catchDisplay';
import './App.css'

function App() {

  const [showAllMovies, setShowAllMovies]=useState(false)

  const allMoviesHandler = () => {
    setShowAllMovies(true)
  }
  return (
   <React.Fragment>
     Your Movies List!
     <div style={{[justify-content]: "center",
  width: "75%",
  display: "block",
  margin: "auto"}}>
     <button onClick={allMoviesHandler}> Search Movies</button>
     <button> Fetch FAVROUITE Movies</button>
     <button> Fetch HATED Movies</button>
     </div>

     {/* <Catchdisplay/> */}

     {showAllMovies && <div>

       <Catchdisplay/>
     </div>}

   </React.Fragment> 

  )
}

export default App;
