import MoviesContext from "./movielist-context"
import {useReducer} from 'react'

const defaultMovieList = {
    movies: []
}

const movieReducer = (state, action) => {
    if(action.type==='ADD')
    {
        const updatedMovies = state.eachMovie.concat(action.eachMovie)
        return{
            eachMovie: updatedMovies
        }
    }
    if(action.type==='REMOVE')
    {
        const existingMovies = state.eachMovie.filter((movie)=>{
            movie.imdbID !== action.eachMovie.imdbID

        })

        console.log(imdbID)

        return{
            eachMovie: existingMovies
        }
    }
    return defaultMovieList
};

const MovielistProvider = (props) => {

    const [movieState, dispatchMovieAction] = useReducer(movieReducer, defaultMovieList);
    const addMovieHandler = eachMovie => {dispatchMovieAction({type:'ADD', eachMovie:eachMovie})}
    const removeMoviesHandler = imdbID => {dispatchMovieAction({type:'REMOVE', imdbID:imdbID})}

    const cartContext = {
        movies: movieState.movies,
        addMovies: addMovieHandler,
        removeMovies:removeMoviesHandler
    }

    return (
        <MoviesContext.Provider value={cartContext}>
            {props.children}
        </MoviesContext.Provider>
    )

}

export default MovielistProvider
