import classes from './likeDisplay.module.css'
import unavailablePoster from "./../img/unavailablePoster.jpeg";

const LikeDisplay =(props)=>{

    const posterSrc =
    localStorage.getItem(props.moviesPoster)=== "N/A" ? unavailablePoster : props.movies.Poster;
  const posterSize = props.movies.Poster === "N/A" ? classes.defaultPoster : "";

    return <React.Fragment>
    <div className={classes.moviesList}>
      <div>
        {<img src={posterSrc} alt="poster" className={posterSize} />}
        <div>{props.movies.Title}</div>

        <div className={classes.eachMovBtn}>
         <button onClick={()=>removelikeHandler(props.movies.imdbID)} className={classes.fav}>Remove Favrouite</button>

        </div>
      </div>
    </div>
    <div>
        {console.log(JSON.parse(localStorage.getItem(key.props)).Title)}
    {localStorage.getItem(key.props).Title}
    {localStorage.getItem(key.props.Type)}
    {localStorage.getItem(key.props.Year)}
    {localStorage.getItem(key.props.Poster)}
    {localStorage.getItem(key.props.imdbId)}  
    </div>
  </React.Fragment>


}

export default LikeDisplay
