import React, {useState, useEffect} from "react"
import classes from './like.module.css'


const Like = (props) => {

    const [allMovies, setAllMovies] = useState([]);
    const [httpError, setHttpError] = useState(false);
    const [loadingError, setloadingError] = useState(true);


    useEffect(() => {
      const fetchMovies = async () => {
        const response = await fetch(
          `https://mymovielist-7f102-default-rtdb.firebaseio.com/likedmovies.json`
        );

        const responseData = await response.json();

        if (responseData.Response === "False") {
          setHttpError(true);
          console.log(responseData)
          throw new Error("Something went wrong");
        }


        console.log("ResonseData:");
        console.log(responseData);
        const movies = responseData.get()
        console.log("++++"+responseData.get())

        setAllMovies(movies);
        setloadingError(false)
      };

      fetchMovies(props.onChange).catch(() => {
        setloadingError(false)
        setHttpError(true)
      });
    }, [props]);

    if (loadingError) {
      return <p className={classes.loadingError}>Loading...</p>;
    }

    if (httpError) {
      return (
        <div className={classes.inputError}>
          <div>Couldn't find any movie of this name. </div>
          <div> Please enter a new movie name.</div>
        </div>
      );
    }



    return(
        <React.Fragment>
            {console.log(allMovies)}
        <div className={classes.allMovies}>
        { allMovies === undefined ? <p> No movies to show!</p> : 
        allMovies.map((movie) => (
            console.log("movie")

        ))}
        </div>

      </React.Fragment>

    )

}

export default Like
